import React, {useState} from "react";
import { Grid, TextField, Button, Typography, Select,MenuItem } from '@material-ui/core'

import {kitchenStatuses} from '../utils/enums'

const AddKitchenForm = props => {
    const [kitcenstatus, updateKitchenStatus] =  useState(null)

    const kitchen_statuses = kitchenStatuses()
    return (
        <Grid container direction="row" justify="space-around" alignItems="center" style={{padding: '5%'}}>
           <Typography variant="h6" style={{color: "#5DB6CE"}}>Add New Kitchen</Typography>
           <TextField label="Kitchen Name" variant="outlined" style={{margin: '1%'}} fullWidth />
           <TextField label="Location" type="text" variant="outlined" style={{margin: '1%'}} fullWidth />
           <TextField label="Cost" type="number" variant="outlined"   style={{margin: '1%'}} fullWidth />
           <Select
              value={kitcenstatus ? kitcenstatus : "default"}
              label="Status"
              variant="outlined"
              onChange={e => updateKitchenStatus(e.target.value)}
              style={{margin: '1%', color: '#aaa'}}
              fullWidth
            >
              <MenuItem value="default" disabled> -- Status -- </MenuItem>
              {
                  Object.keys(kitchen_statuses).map((status, i) => <MenuItem value={kitchen_statuses[status].value}>{kitchen_statuses[status].label}</MenuItem>)
              }

            </Select>
           <Button style={{backgroundColor: '#5DB6CE', color: '#fff', margin: '2%', width: '100%'}}>Submit</Button>
        </Grid>
    )
}


export default AddKitchenForm;
