import React from "react";
import { Grid, Dialog, Button, Typography } from '@material-ui/core'
import { AddKitchenForm } from './forms'


const AddKitchenModal = props => {

    let { add_kitchen, closeAppModal } = props

    return (
        <Dialog fullWidth open={(add_kitchen) ? true : false} onClose={() => closeAppModal()}>
          <Grid style={{margin: '2%'}}>
            <AddKitchenForm />
          </Grid>
        </Dialog>
    )
}

const DeleteConfirmationModal = props => {
    let { delete_kitchen, closeDeleteModal } = props
    return (
        <Dialog fullWidth open={(delete_kitchen) ? true : false} onClose={() => closeDeleteModal()}>
          <Grid container direction="column" alignItems="center" justify="space-evenly">
              <Typography variant="h6" style={{color: "#5DB6CE", margin: '1%'}}>Are you sure you want to delete?</Typography>
              <Typography variant="p" style={{color: "#aaa", margin: '1%'}}>Please confirm kitchen deletion</Typography>
              <Button size="small" style={{backgroundColor: '#990000', color: '#fff', margin: '1%'}}>Yes</Button>
              <Button size="small" style={{backgroundColor: '#aaa', color: '#fff', margin: '1%'}}>No</Button>
          </Grid>
        </Dialog>
    )
}


export { AddKitchenModal, DeleteConfirmationModal };
